        SpeechSynthesizer _synthesizer = null;
        string strTalkingText;
        bool isfavmode = false;
        char[] favslist = new char[622];
        int lastfavposition = 0;
        int newfavposition = 0;
        // Constructor
        public DetailsPage()
        {
            InitializeComponent();
            if (_synthesizer == null)
            {
                _synthesizer = new SpeechSynthesizer();
            }
            else
            {
                _synthesizer.CancelAll();
            }
            DispatcherTimer dt = new DispatcherTimer();
            dt.Interval = new TimeSpan(0, 0, 0, 0, 45000); // 60 Seconds  
            dt.Tick += new EventHandler(dt_Tick);
            dt.Start(); 
        }
        
        private void CancelSynthesizer()
        {
            _synthesizer.CancelAll();
            ApplicationBarIconButton btn = (ApplicationBarIconButton)ApplicationBar.Buttons[1];
            btn.Text = "Read";
            btn.IconUri = new Uri("transport.play.png", UriKind.Relative);
        }
        protected override void OnNavigatedTo(NavigationEventArgs e)
        {

            SetBackgroundColor();            
            string selectedMode = "";
            NavigationContext.QueryString.TryGetValue("selectedMode", out selectedMode);
            QuotedBy.TextAlignment = TextAlignment.Right;
            for(int i=0;i<621;i++)
            {
                if (App.favoritesetting[i] == '1')
                {
                    favslist[i] = '1';
                }
                else
                    favslist[i] = '0';
            }
            if(selectedMode.Contains("Regular"))
            {
                isfavmode = false;
                ApplicationBarIconButton btn = (ApplicationBarIconButton)ApplicationBar.Buttons[2];
                btn.Text = "Add";
                btn.IconUri = new Uri("favs.addto.png", UriKind.Relative);
            }
            else if (selectedMode.Contains("favorite"))
            {
                isfavmode = true;
                ApplicationBarIconButton btn = (ApplicationBarIconButton)ApplicationBar.Buttons[2];
                btn.Text = "Remove";
                btn.IconUri = new Uri("cancel.png", UriKind.Relative);
                SetFavoritePosition(0);  
            }

            MyNavigate();
           // QuoteText.Text = quotelist[App.lastquotepage * 2];
            //QuotedBy.Text = quotelist[App.lastquotepage * 2 +1];
  
            MSAdControlAd1.AdRefreshed += MSAdControl_NewAd;

            MSAdControlAd1.ErrorOccurred += MSAdControl1_AdControlError;

            MSAdControlAd2.AdRefreshed += MSAdControl_NewAd;
            MSAdControlAd2.ErrorOccurred += MSAdControl2_AdControlError;
        }

        private void SetBackgroundColor()
        {
            string hexCode = App.colorstring;
            hexCode = hexCode.TrimStart('#');
            Color _color = new Color();
            _color.A = byte.Parse(hexCode.Substring(0, 2), NumberStyles.AllowHexSpecifier);
            _color.R = byte.Parse(hexCode.Substring(2, 2), NumberStyles.AllowHexSpecifier);
            _color.G = byte.Parse(hexCode.Substring(4, 2), NumberStyles.AllowHexSpecifier);
            _color.B = byte.Parse(hexCode.Substring(6, 2), NumberStyles.AllowHexSpecifier);

            LayoutRoot.Background = new SolidColorBrush(_color);

        }
      private void SetFavoritePosition(int mode)
      {
          int i;
          lastfavposition = newfavposition;
          if (mode != 2)
          {
              for (i=newfavposition+1; i < 621; i++)
              {
                  if (favslist[i] == '1')
                  {
                      newfavposition = i;
                      return;
                  }
              }
              for(i =0;i<621;i++)
              {
                  if (favslist[i] == '1')
                  {
                      newfavposition = i;
                      return;
                  }
              }
              
          }
          else
          {
              for (i=newfavposition-1; i >0; i--)
              {
                  if (favslist[i] == '1')
                  {
                      newfavposition = i;
                      return;
                  }
              }
              for (i = 621; i >0; i--)
              {
                  if (favslist[i] == '1')
                  {
                      newfavposition = i;
                      return;
                  }
              }

          }
      }
        private async void Play_Click(object sender, EventArgs e)
        {
            ApplicationBarIconButton btn = (ApplicationBarIconButton)ApplicationBar.Buttons[1];

            if (btn.Text == "Read")
            {
                btn.Text = "Stop";
                btn.IconUri = new Uri("cancel.png", UriKind.Relative);
                try
                {
                    await _synthesizer.SpeakTextAsync(strTalkingText);
                }
                catch(Exception)
                {
                    CancelSynthesizer();
                }
            }
            else if (btn.Text == "Stop")
            {
                CancelSynthesizer();
            }
        }
        private void Email_Click(object sender, EventArgs e)
        {
            EmailComposeTask emailcomposetask = new EmailComposeTask();
            emailcomposetask.Subject = "QuotesnQuotes Windows App Quote " ;
            emailcomposetask.Body = strTalkingText ;

            emailcomposetask.Show();

        }
        private void SMS_Click(object sender, EventArgs e)
        {
            SmsComposeTask smscomposetask = new SmsComposeTask();
          
            smscomposetask.Body = strTalkingText;

            smscomposetask.Show();

        }
        private void Share_Click(object sender, EventArgs e)
        {
            ShareStatusTask sharecomposetask = new ShareStatusTask();
           // emailcomposetask.Subject = "QuotesnQuotes Windows App Quote ";
            sharecomposetask.Status = strTalkingText;

            sharecomposetask.Show();

        }

        protected override void OnBackKeyPress(System.ComponentModel.CancelEventArgs e)
        {

            _synthesizer.CancelAll();

            // Note that we don't set e.Cancel=true, to enable back press to implement page Goback.          

            base.OnBackKeyPress(e);  // Call base

        }
        private void MoveNext()
        {

            if (!isfavmode)
            {
                if (App.lastquotepage == 621)
                    App.lastquotepage = 0;
                else
                    App.lastquotepage++;
            }
            else
            {
                SetFavoritePosition(1);
            }
        
            MyNavigate();
        }
        private void MoveBack()
        {
            if (!isfavmode)
            {
                if (App.lastquotepage == 0)
                    App.lastquotepage = 621;
                else
                    App.lastquotepage--;
            }
            else
            {
                SetFavoritePosition(2);
            }
            MyNavigate();
        }
        private void MyNavigate()
        {
            CancelSynthesizer();
            if (!isfavmode)
            {
                QuoteText.Text = quotelist[App.lastquotepage * 2];
                QuotedBy.Text = quotelist[App.lastquotepage * 2 + 1];
                strTalkingText = quotelist[App.lastquotepage * 2] + "  By   " + quotelist[App.lastquotepage * 2 + 1];
            }
            else
            {
                QuoteText.Text = quotelist[newfavposition * 2];
                QuotedBy.Text = quotelist[newfavposition * 2 + 1];
                strTalkingText = quotelist[newfavposition * 2] + "  By   " + quotelist[newfavposition * 2 + 1];
            }

        }
        private void OnFlick(object sender, FlickGestureEventArgs e)
        {
            string str = string.Format("{0} Flick: Angle {1} Velocity {2},{3}",
                e.Direction, Math.Round(e.Angle), e.HorizontalVelocity, e.VerticalVelocity);
            if (e.HorizontalVelocity < -300)
            {
                MoveNext();
            }
            else if (e.HorizontalVelocity > 300)
            {
                MoveBack();
            }
        }

        private void Back_Click(object sender, EventArgs e)
        {
            MoveBack();
        }
        private void Next_Click(object sender, EventArgs e)
        {
            MoveNext();

        }
        void MSAdControl_NewAd(object sender, System.EventArgs e)
        {

            try
            {
                MSAdControlAd1.Visibility = Visibility.Visible;
                MSAdControlAd2.Visibility = Visibility.Visible;
                AdDuplexAdControl.Visibility = Visibility.Collapsed;
                InneractiveXamlAd.Visibility = Visibility.Collapsed;
            }
            catch { ;}

        }



        void MSAdControl1_AdControlError(object sender, Microsoft.Advertising.AdErrorEventArgs e)
        {

            MSAdControlAd1.Visibility = Visibility.Collapsed;

            AdDuplexAdControl.Visibility = Visibility.Visible;

        }
        void MSAdControl2_AdControlError(object sender, Microsoft.Advertising.AdErrorEventArgs e)
        {

            MSAdControlAd2.Visibility = Visibility.Collapsed;

            InneractiveXamlAd.Visibility = Visibility.Visible;

        }


        void dt_Tick(object sender, EventArgs e)
        {
            try
            {

                if (MSAdControlAd1.Visibility != Visibility.Visible)
                    MSAdControlAd1.Visibility = Visibility.Visible;
                MSAdControlAd1.Refresh();
                if (MSAdControlAd2.Visibility != Visibility.Visible)
                    MSAdControlAd2.Visibility = Visibility.Visible;
                MSAdControlAd2.Refresh();


            }
            catch { ;}

        }

        private void AddtoFavsClick(object sender, EventArgs e)
        {
            if(isfavmode)
            {
                favslist[newfavposition] = '0';
            }
            else
            {
                favslist[App.lastquotepage] =  '1';
            }
            string str = new string(favslist);
            App.favoritesetting = str;
        }          
    }
}